//function for upcasing
string upcase(string op_str) {
	string opt_str="";
	for (int i=0;i<op_str.length();++i){
		if (op_str.at(i) >= 'a' && op_str.at(i) <= 'z')
			opt_str+=op_str.at(i)+('A'-'a');
		else
			opt_str+=op_str.at(i);
	}
	return opt_str;
}
